public class QuickSort{public static void quickSort(int[]arr,int low,int high){if(low<high){int spot=partition(arr,low,high);quickSort(arr,low,spot);quickSort(arr,spot+1,high);}}public static int partition(int[]arr,int low,int high){int pivot=arr[low];int bot=low-1;int top=high+1;while(bot<top){while(arr[--top]>pivot);while(arr[++bot]<pivot);if(bot>=top)return top;int temp=arr[bot];arr[bot]=arr[top];arr[top]=temp;}return-1;}}
